import React, { useState, useContext } from 'react';
import { Modal, TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { NodeContext } from '../NodeContext.js';
import { generateTwoLayers } from '../api/apiCalls.js';
import "../App.css"
import { CenterFocusStrong } from '@material-ui/icons';

const useStyles = makeStyles(theme => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: 'rgba(255, 255, 255, 0.7)',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  },
}));

const PromptBar = ({ onClose }) => {
  const { setTreeData } = useContext(NodeContext);

  const classes = useStyles();
  const [loading, setLoading] = useState(false);

  const handleSearch = async (event) => {
    if (event.key === 'Enter') {
      setLoading(true);

      generateTwoLayers(event.target.value)
        .then(response => {
          console.log(response);  
          setTreeData(response); 
          if (response.error) {
            console.log(response.error);
          }

          setLoading(false);
          onClose();
        })
        .catch(error => {
          // Handle your error here
          console.log(error);
        });
    }
  };


  return (
    loading ?
        <Modal open={true} onClose={onClose} style={{ display: "flex", justifyContent: 'center', alignItems: "center", height: "100%", width: "100%"}}>
          <div className='spinner'>
            <div className="lds-roller"><div></div><div></div><div></div><div></div><div></div><div></div><div></div><div></div></div>
          </div>
        </Modal>
      :
      <Modal open={true} onClose={onClose}>
        <div className={classes.paper} style={{ borderRadius: "6px" }}>
          <TextField fullWidth placeholder="I want to become an expert in..." autoFocus onKeyDown={handleSearch} />
        </div>
      </Modal>
  );
};

export default PromptBar;
