import React, { useContext, useEffect, useState } from 'react';
import { NodeContext } from '../NodeContext.js';
import Modal from '@mui/material/Modal'
import './styles/ExpandedNode.css';
import { Close } from '@material-ui/icons';

function ExpandedNode({ children }) {
  const { currentNode, loading, treeData, expandedNode } = useContext(NodeContext);
  const [foundNode, setFoundNode] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const findNode = (node, id) => {
    if (node.id === id) {
      return node;
    }

    if (node.children) {
      for (let child of node.children) {
        const result = findNode(child, id);
        if (result) {
          return result;
        }
      }
    }

    return null;
  };

  useEffect(() => {
    if (currentNode && treeData) {
      setFoundNode(findNode(treeData, currentNode.data.id));
    }
  }, [currentNode, treeData]);

  useEffect(() => {
    if (expandedNode == true) {
      setModalOpen(true)
    }
  }, [expandedNode])

  const _onClose = () => setModalOpen(false);

  return (
    treeData &&
    (
      <Modal open={modalOpen} close={_onClose} class="modal"
      >
        <div className='expandedNode'>
          {loading && <div className='noNodeTag'>Loading...</div>}
          {!loading && foundNode &&
            <div>
              <div className="nodeTag" style={{display: "flex", alignItems: "center",justifyContent: "space-between", }}><h3>{foundNode.name}</h3>
              <button onClick={_onClose} style={{ background: "none", border: "none", cursor: "pointer", color: 'white' }}>
    <Close />
  </button>

              </div>
              <div className="nodeTag"><b>Description:</b> {foundNode.description}</div>
              <StyledButton><a href={foundNode?.referenceUrl} target='_blank'>Open paper</a></StyledButton>
            </div>
          }
          {children}
        </div>
      </Modal>
    )
  );
}

export default ExpandedNode;

const StyledButton = styled(button)`
  backgroundColor: "pink",
`
