import React, { useContext, useEffect, useState } from 'react';
import Tree from 'react-d3-tree';
import cloneDeep from 'lodash/cloneDeep';
import { NodeContext } from '../NodeContext.js';
import { getLongDescription, expandGraphWithNewNodes } from '../api/apiCalls.js'; // or wherever your API functions are stored
import './styles/Graph.css';

// Helper functions
function mergeData(nodeId, treeData, expandedData) {
  // Function to recursively search for a node
  function findNode(node) {
    if (node.id === nodeId) {
      // Node found, append children
      node.children = node.children?.concat(expandedData);
    } else if (node.children) {
      // Node not found, search children
      node.children.forEach(findNode);
    }
  }

  // Make a deep copy of the tree data and search it
  const newTreeData = cloneDeep(treeData);
  findNode(newTreeData);

  return newTreeData;
}

// Function to recursively update node description
function updateDescription(node, currentNode, longDescription) {
  console.log(currentNode);
  if (node.id === currentNode.id) {
    // Return a new object for the node
    return { ...node, description: longDescription };
  }
  if (node.children) {
    // Iterate through children, creating new objects for any that have changed
    return {
      ...node,
      children: node.children.map(child => updateDescription(child, currentNode, longDescription)),
    };
  }
  // No changes to this node or its children
  return node;
};

const getLongDesc = async (node, callbackFunc, currentTreeData) => {
  return new Promise(async (resolve, reject) => {
    try {
      console.log(currentTreeData.name)
      const paperInsights = await getLongDescription(node, node.data.name === "Residual calcium effects"); // Add any necessary arguments
      // Update node description in treeData
      const updatedTreeData = updateDescription(currentTreeData, node.data, paperInsights);
      callbackFunc(updatedTreeData);
      resolve(updatedTreeData); // Return the updated data
    } catch (error) {
      reject(error);
    }
  });
}

const expandGraph = async (node, callbackFunc, currentTreeData) => {
  const expandedGraphData = await expandGraphWithNewNodes(node.data.id, currentTreeData.name, node,);
  const newTreeData = mergeData(node.data.id, currentTreeData, expandedGraphData);
  callbackFunc(newTreeData)
}

// Main component
const Graph = () => {
  const { scale, translate, handleNodeClick, currentNode, loading, setLoading, expandedNode, treeData, setTreeData } = useContext(NodeContext);

  useEffect(() => {
    if (!currentNode) return;

    const fetchData = async () => {
      try {
        if (currentNode && currentNode.data) {
          setLoading(true);
          const updatedTreeData = await getLongDesc(currentNode, setTreeData, treeData);
          await expandGraph(currentNode, setTreeData, updatedTreeData);
          setLoading(false);
        }
      } catch (error) {
        console.error('An error occurred while fetching data:', error);
      }
    };

    fetchData();
  }, [currentNode]);


  const onClick = (nodeData) => {
    handleNodeClick(nodeData);
  };

  const [foundNode, setFoundNode] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const findNode = (node, id) => {
    if (node.id === id) {
      return node;
    }

    if (node.children) {
      for (let child of node.children) {
        const result = findNode(child, id);
        if (result) {
          return result;
        }
      }
    }

    return null;
  };

  useEffect(() => {
    if (currentNode && treeData) {
      setFoundNode(findNode(treeData, currentNode.data.id));
    }
  }, [currentNode, treeData]);

  useEffect(() => {
    if (expandedNode) {
      setModalOpen(true)
    }
  }, [expandedNode])

  const _onClose = () => setModalOpen(false);


  return (<div className="graph" style={{ transform: `scale(${scale})` }}>
    <Modal open={modalOpen} close={_onClose}
    >
      <div className='expandedNode'>
        {loading && <div className='noNodeTag'>Loading...</div>}
        {!loading && foundNode &&
          <div>
            <div className="nodeTag" style={{ display: "flex", alignItems: "center", justifyContent: "space-between", }}><h3>{foundNode.name}</h3>
              <button onClick={_onClose} style={{ background: "none", border: "none", cursor: "pointer", color: 'white' }}>
                <Close />
              </button>

            </div>
            <div className="nodeTag">{foundNode.description}</div>
            <button className="nodeTag" style={{ background: "#758dbe", border: "#758dbe", cursor: "pointer", color: 'white', width: '100px', height: '25px', textAlign: 'center' }}>
              <a href={foundNode?.referenceUrl} target='_blank'>Open paper</a>
            </button>
          </div>
        }
        {children}
      </div>
    </Modal>
    <div className="helperText"><i>Use cmd-s (in mac) or ctrl-q (in windows) to open the prompt editor.</i></div>
    {treeData && (
      <Tree
        data={treeData}
        translate={translate}
        onNodeClick={onClick}
        separation={{ siblings: 1, nonSiblings: 1 }}
        // pathFunc="step"
        rootNodeClassName="node__root"
        branchNodeClassName="node__branch"
        leafNodeClassName="node__leaf"
      />
    )}
  </div>
  );
};

export default Graph;
